import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles/slideshow.css';

const Slideshow = () => {
  const [images, setImages] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [containerHeight, setContainerHeight] = useState('50vh'); // 初期値を設定
  const [showImage, setShowImage] = useState(false);

  useEffect(() => {
    const fetchImages = async () => {
      try {
        const response = await axios.get('https://kaitoportfolio.microcms.io/api/v1/portfolio', {
          headers: {
            'X-API-KEY': 'D47K5TpPZfRzZCyXPPs2YxML9X5uOJyWAQdZ'
          }
        });
        setImages(response.data.contents);
      } catch (error) {
        console.error('Error fetching data from MicroCMS:', error);
      }
    };

    fetchImages();
  }, []);

  const handleImageLoad = (e) => {
    const { height } = e.target;
    setContainerHeight(`${height}px`); // 画像の高さをコンテナに設定
    setShowImage(true);
  };

  if (images.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div className="imgContainer" style={{ height: containerHeight }}>
      <div className="LeftArea">
        {/* 左側のコードは省略 */}
      </div>

      <div className="RightArea">
        <div className="Area3">
          {textComplete && (
            <img
              src={images[currentIndex].src.url}
              alt={images[currentIndex].title}
              className={`imgArea ${showImage ? 'show' : ''}`}
              onLoad={handleImageLoad} // 画像の読み込み完了時に高さを設定
            />
          )}
        </div>
        <div className="Area4">
          {/* 他のコンテンツ */}
        </div>
      </div>
    </div>
  );
};

export default Slideshow;
