import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import './styles/slideshow.css';

gsap.registerPlugin(ScrollTrigger);

const Slideshow = () => {
  const [images, setImages] = useState([]);
  const containerRef = useRef(null);

  useEffect(() => {
    // MicroCMSからデータを取得する関数
    const fetchImages = async () => {
      try {
        const response = await axios.get('https://kaitoportfolio.microcms.io/api/v1/portfolio', {
          headers: {
            'X-API-KEY': 'D47K5TpPZfRzZCyXPPs2YxML9X5uOJyWAQdZ'
          }
        });
        setImages(response.data.contents);
      } catch (error) {
        console.error('Error fetching data from MicroCMS:', error);
      }
    };

    fetchImages();
  }, []);

  useEffect(() => {
    if (images.length > 0) {
      gsap.to(containerRef.current, {
        scrollTrigger: {
          trigger: containerRef.current,
          start: 'top top',
          end: `+=${images.length * 100}vh`,
          pin: true,
          scrub: true,
          markers: true,
          onUpdate: (self) => {
            const index = Math.floor(self.progress * images.length);
            setCurrentIndex(index);
          }
        }
      });
    }
  }, [images]);

  const [currentIndex, setCurrentIndex] = useState(0);

  if (images.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div ref={containerRef}>
      <div className="imgContainer">
        {images.map((image, index) => (
          <div
            key={index}
            className={`imgSlide ${index === currentIndex ? 'active' : ''}`}
          >
            <img
              src={image.src.photo}
              alt={image.title}
              className="imgArea"
            />
            <div className="overviewArea">
              <div className="left">
                <h2 className="title">{image.title}</h2>
                <h3 className="hurigana">{image.hurigana}</h3>
              </div>
              <div className="right">
                <div className="place">
                  <img src='/img/map.png' alt="map" />
                  <h2>{image.place}</h2>
                </div>
                <h2 className="day">{image.day}</h2>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Slideshow;
