import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles/slideshow.css';

const Slideshow = () => {
  const [images, setImages] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    // MicroCMSからデータを取得する関数
    const fetchImages = async () => {
      try {
        const response = await axios.get('https://YOUR_MICROCMS_ENDPOINT', {
          headers: {
            'X-API-KEY': 'YOUR_API_KEY'
          }
        });
        setImages(response.data.contents);
      } catch (error) {
        console.error('Error fetching data from MicroCMS:', error);
      }
    };

    fetchImages();

    const interval = setInterval(() => {
      setCurrentIndex(prevIndex => (prevIndex + 1) % images.length);
    }, 3000); // 3秒ごとに切り替え

    return () => clearInterval(interval);
  }, [images.length]);

  if (images.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div className="slideshow-container">
      <div className="slideshow-wrapper" style={{ transform: `translateX(${-currentIndex * 100}%)` }}>
        {images.map((image, index) => (
          <img
            key={index}
            src={image.src.url}
            alt={image.title}
            className="slideshow-image"
          />
        ))}
      </div>
      <div className="overviewArea">
        <div className="left">
          <h2 className="title">{images[currentIndex].title}</h2>
          <h3 className="hurigana">{images[currentIndex].hurigana}</h3>
        </div>
        <div className="right">
          <div className="place">
            <img src='/img/map.png' alt="map"/>
            <h2>{images[currentIndex].place}</h2>
          </div>
          <h2 className="day">{images[currentIndex].day}</h2>
        </div>
      </div>
    </div>
  );
};

export default Slideshow;
