import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles/slideshow.css';
import imgBackText from './imgBackText'; // インポート

const Slideshow = () => {
  const [images, setImages] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [show, setShow] = useState(false);
  const [visibleLines, setVisibleLines] = useState([]);
  const [textComplete, setTextComplete] = useState(false);

  useEffect(() => {
    // MicroCMSからデータを取得する関数
    const fetchImages = async () => {
      try {
        const response = await axios.get('https://kaitoportfolio.microcms.io/api/v1/portfolio', {
          headers: {
            'X-API-KEY': 'D47K5TpPZfRzZCyXPPs2YxML9X5uOJyWAQdZ'
          }
        });
        setImages(response.data.contents);
      } catch (error) {
        console.error('Error fetching data from MicroCMS:', error);
      }
    };

    fetchImages();
  }, []);

  useEffect(() => {
    const lines = imgBackText.split('\n');
    let currentLine = 0;

    const interval = setInterval(() => {
      setVisibleLines(prevLines => [...prevLines, lines[currentLine]]);
      currentLine += 1;

      if (currentLine >= lines.length) {
        clearInterval(interval);
        setTextComplete(true);
      }
    }, 100); // 行を表示する間隔

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    if (textComplete) {
      setShow(true);
      const interval = setInterval(() => {
        setShow(false);
        setTimeout(() => {
          setCurrentIndex(prevIndex => (prevIndex + 1) % images.length);
          setShow(true);
        }, 500); // アニメーション時間（0.5秒）に合わせる
      }, 20000); // 20秒ごとに切り替え

      return () => clearInterval(interval);
    }
  }, [textComplete, images.length]);

  if (images.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <div className="imgContainer">
        <div className="imgBackText">
          {visibleLines.map((line, index) => (
            <h3 key={index} className="line">
              {line}
            </h3>
          ))}
        </div>
        {textComplete && (
          <img
            src={images[currentIndex].src.url}
            alt={images[currentIndex].title}
            className={`imgArea ${show ? 'show' : ''}`}
          />
        )}
      </div>
    </div>
  );
};

export default Slideshow;
