import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import './styles/slideshow.css';

gsap.registerPlugin(ScrollTrigger);

const Slideshow = () => {
  const [images, setImages] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const imgContainerRef = useRef(null);

  useEffect(() => {
    const fetchImages = async () => {
      try {
        const response = await axios.get('https://kaitoportfolio.microcms.io/api/v1/portfolio', {
          headers: {
            'X-API-KEY': 'D47K5TpPZfRzZCyXPPs2YxML9X5uOJyWAQdZ'
          }
        });
        setImages(response.data.contents);
      } catch (error) {
        console.error('Error fetching data from MicroCMS:', error);
      }
    };

    fetchImages();
  }, []);

  useEffect(() => {
    if (images.length === 0) return;

    const tl = gsap.timeline({
      scrollTrigger: {
        trigger: imgContainerRef.current,
        start: 'top center',
        end: 'bottom center',
        scrub: true,
        onEnter: () => handleNextSlide(),
        onEnterBack: () => handlePrevSlide()
      }
    });

    tl.to(".imgArea", { opacity: 0, duration: 1 })
      .to(".imgArea", { opacity: 1, duration: 1 });

    return () => {
      if (tl.scrollTrigger) tl.scrollTrigger.kill();
    };
  }, [images]);

  const handleNextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  if (images.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <div className="imgContainer" ref={imgContainerRef}>
        {images.map((photo, index) => (
          <img
            key={photo}
            src={photo.src.url}
            alt={photo.title}
            className={`imgArea ${index === currentIndex ? 'active' : ''}`}
          />
        ))}
      </div>
      {/* <div className="overviewArea">
        <div className="left">
          <h2 className={`title ${currentIndex === index ? 'show' : ''}`}>
            {images[currentIndex].title}
          </h2>
          <h3 className={`hurigana ${currentIndex === index ? 'show' : ''}`}>
            {images[currentIndex].hurigana}
          </h3>
        </div>
        <div className="right">
          <div className={`place ${currentIndex === index ? 'show' : ''}`}>
            <img src='/img/map.png' alt="map"/>
            <h2>{images[currentIndex].place}</h2>
          </div>
          <h2 className={`day ${currentIndex === index ? 'show' : ''}`}>{images[currentIndex].day}</h2>
        </div>
      </div> */}
    </div>
  );
};

export default Slideshow;
