import React, { useState, useEffect } from 'react';
import './styles/slideshow.css';

const images = [
  { src: '/img/P1050621.jpg', title: '京都', hurigana: 'kyoto',place: 'Kyoto Kiyomizu', day: '2023.11.23' },
  { src: '/img/P1050817.jpg', title: '斜光', hurigana: 'Syakou',place: 'Aichi Sakae', day: '2023.08.23'},
  { src: '/img/P1063066.jpg', title: '祝日', hurigana: 'Syukujitu',place: 'Aichi Sakae', day: '2023.08,06'}
];

const Slideshow = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [show, setShow] = useState(true);

  useEffect(() => {
    const interval = setInterval(() => {
      setShow(false);
      setTimeout(() => {
        setCurrentIndex(prevIndex => (prevIndex + 1) % images.length);
        setShow(true);
      }, 1000); // アニメーション時間（1秒）に合わせる
    }, 30000); // 3秒ごとに切り替え

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="imgContainer">
        <img
            src={images[currentIndex].src}
            alt={images[currentIndex].title}
            className={`imgArea ${show ? 'show' : ''}`}
        />

        <div className="overviewArea">
            <div className="left">
                <h2 className={`title ${show ? 'show' : ''}`}>
                    {images[currentIndex].title}
                </h2>

                <h3 className={`hurigana ${show ? 'show' : ''}`}>
                    {images[currentIndex].hurigana}
                </h3>
            </div>

            <div className="right">
                <div className="{`place ${show ? 'show' : ''}`}">
                    <img src='/img/map.png'></img>
                    <h2>{images[currentIndex].day}</h2>
                </div>

                <h2 className="{`day ${show ? 'show' : ''}`}"></h2>
            </div>
        </div>
    </div>

  );
};

export default Slideshow;
