import  React, { useState, useEffect } from 'react';

const images = [
    {src: '../assets/img/P1050621.jpg', title: 'Title1'},
    {src: '../assets/img/P1050817.jpg', title: 'Title2'},
    {src: '../assets/img/P1063066.jpg', title: 'Title3'},
];

const Slideshow = () => {
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        const interval = SetInterval(() => {
            setCurrentIndex(preIndex => (preIndex + 1) % images.length);
        }, 3000);//とりあえず3秒ごと
        return () => clearInterval(interval);
    }, []);

    return (
        <div className="slideshow-container">
            <img src={images[currentIndex].src} alt={images[currentIndex].title} className="slideshow-image" />
            <h1 className="slideshow-title">{images[currentIndex].title}</h1>
        </div>
    ) 
}

export default Slideshow;
