import React, { useState, useEffect } from 'react';
import axios from 'axios';
import gsap from 'gsap';
import './styles/slideshow.css';

const Slideshow = () => {
  const [images, setImages] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const fetchImages = async () => {
      try {
        const response = await axios.get('https://kaitoportfolio.microcms.io/api/v1/portfolio', {
          headers: {
            'X-API-KEY': 'D47K5TpPZfRzZCyXPPs2YxML9X5uOJyWAQdZ',
          },
        });
        setImages(response.data.contents);
      } catch (error) {
        console.error('Error fetching data from MicroCMS:', error);
      }
    };

    fetchImages();

    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 20000);

    return () => clearInterval(interval);
  }, [images.length]);

  useEffect(() => {
    if (images.length > 0) {
      const tl = gsap.timeline();

      tl.to('.imgArea', { opacity: 1, duration: 1 })
        .to('.title, .hurigana', { opacity: 0, duration: 0.5 })
        .to('.title, .hurigana', { opacity: 1, duration: 0.5, delay: 0.5 })
        .to('.place, .day', { opacity: 0, duration: 0.5 })
        .to('.place, .day', { opacity: 1, duration: 0.5, delay: 0.5 });
    }
  }, [currentIndex, images]);

  if (images.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <div className="imgContainer">
        <img
          src={images[currentIndex].src.url}
          alt={images[currentIndex].title}
          className="imgArea"
          style={{ opacity: 0 }}
        />
      </div>
      <div className="overviewArea">
        <div className="left">
          <h2 className="title" style={{ opacity: 0 }}>
            {images[currentIndex].title}
          </h2>
          <h3 className="hurigana" style={{ opacity: 0 }}>
            {images[currentIndex].hurigana}
          </h3>
        </div>
        <div className="right">
          <div className="place" style={{ opacity: 0 }}>
            <img src="/img/map.png" alt="map" />
            <h2>{images[currentIndex].place}</h2>
          </div>
          <h2 className="day" style={{ opacity: 0 }}>
            {images[currentIndex].day}
          </h2>
        </div>
      </div>
    </div>
  );
};

export default Slideshow;
