import React, { useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../styles/header.css';

const Header = () => {
  const location = useLocation();

  useEffect(() => {
    const header = document.querySelector('.headerContainer');
    const links = document.querySelectorAll('.HeaderNav h1 a');

    if (location.pathname === '/photo') {
      header.style.backgroundColor = '#000'; // 背景色
      header.style.color = '#fff'; // テキスト色
      links.forEach(link => link.style.color = '#fff'); // リンクのテキスト色
    } else {
      header.style.backgroundColor = ''; // 元の色に戻す
      header.style.color = ''; // 元の色に戻す
      links.forEach(link => link.style.color = ''); // リンクのテキスト色を元に戻す
    }
  }, [location]);

  return (
    <div className="headerContainer">
      <div className="mainTitle">
        <h1>
          <Link to='/'>KAITO MATSUDA<br className="titleBr" /> PORTFOLIO SITE</Link>
        </h1>
      </div>
      <div className="sideLink">
        {/* <div className="HeaderNav"><h1><Link to='/works'>WOrks</Link></h1></div> */}
        <div className="HeaderNav"><h1><Link to='/about'>ABout</Link></h1></div>
        <div className="HeaderNav"><h1><Link to='/photo'>PHoto</Link></h1></div>
        <div className="HeaderNav"><h1><Link to='/movie'>MOvie</Link></h1></div>
      </div>
    </div>
  );
};

export default Header;
