import React, { useState, useEffect } from 'react';
import axios from 'axios';
import gsap from 'gsap';
import './styles/slideshow.css';

const shuffleText = (text) => {
  let shuffled = text.split('');
  for (let i = shuffled.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
  }
  return shuffled.join('');
};

const splitText = (text) => {
  return text.split('').map((char, index) => (
    <span key={index} className="char">
      {char}
    </span>
  ));
};

const Slideshow = () => {
  const [images, setImages] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [show, setShow] = useState(true);

  useEffect(() => {
    const fetchImages = async () => {
      try {
        const response = await axios.get('https://kaitoportfolio.microcms.io/api/v1/portfolio', {
          headers: {
            'X-API-KEY': 'D47K5TpPZfRzZCyXPPs2YxML9X5uOJyWAQdZ',
          },
        });
        setImages(response.data.contents);
      } catch (error) {
        console.error('Error fetching data from MicroCMS:', error);
      }
    };

    fetchImages();

    const interval = setInterval(() => {
      setShow(false);
      setTimeout(() => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
        setShow(true);
      }, 1000);
    }, 20000);

    return () => clearInterval(interval);
  }, [images.length]);

  useEffect(() => {
    if (images.length > 0) {
      const tl = gsap.timeline();
      const originalTitle = images[currentIndex].title;
      const originalHurigana = images[currentIndex].hurigana;
      const originalPlace = images[currentIndex].place;
      const originalDay = images[currentIndex].day;

      tl.to('.imgArea', { opacity: 1, duration: 1 })
        .to('.imgArea', { opacity: 1, duration: 1 }) // Keeps the image displayed for 1 second
        .to('.title .char, .hurigana .char', { opacity: 0, duration: 0.5, stagger: 0.02 })
        .to('.title .char, .hurigana .char', {
          opacity: 1,
          duration: 0.5,
          stagger: 0.02,
          textContent: shuffleText(originalTitle),
          ease: 'none',
          onComplete: () => {
            document.querySelector('.title').innerHTML = splitText(originalTitle);
            document.querySelector('.hurigana').innerHTML = splitText(originalHurigana);
          },
        })
        .to('.place .char, .day .char', { opacity: 0, duration: 0.5, stagger: 0.02 }, '+=0.5')
        .to('.place .char, .day .char', {
          opacity: 1,
          duration: 0.5,
          stagger: 0.02,
          textContent: shuffleText(originalPlace),
          ease: 'none',
          onComplete: () => {
            document.querySelector('.place h2').innerHTML = splitText(originalPlace);
            document.querySelector('.day').innerHTML = splitText(originalDay);
          },
        });
    }
  }, [currentIndex, show]);

  if (images.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <div className="imgContainer">
        <img
          src={images[currentIndex].src.url}
          alt={images[currentIndex].title}
          className="imgArea"
          style={{ opacity: 0 }}
        />
      </div>
      <div className="overviewArea">
        <div className="left">
          <h2 className="title">
            {splitText(images[currentIndex].title)}
          </h2>
          <h3 className="hurigana">
            {splitText(images[currentIndex].hurigana)}
          </h3>
        </div>
        <div className="right">
          <div className="place">
            <img src="/img/map.png" alt="map" />
            <h2>{splitText(images[currentIndex].place)}</h2>
          </div>
          <h2 className="day">
            {splitText(images[currentIndex].day)}
          </h2>
        </div>
      </div>
    </div>
  );
};

export default Slideshow;
