import React, { useState, useEffect } from 'react';
import axios from 'axios';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import './styles/slideshow.css';
import imgBackText from './imgBackText'; // インポート

const shuffleText = (text) => {
  let shuffled = text.split('');
  for (let i = shuffled.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
  }
  return shuffled.join('');
};

const splitText = (text) => {
  return text.split('').map((char, index) => (
    <span key={index} className="char">
      {char}
    </span>
  ));
};

const Slideshow = () => {
  const [images, setImages] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [show, setShow] = useState(true);
  const [shuffledBackText, setShuffledBackText] = useState('');

  useEffect(() => {
    // MicroCMSからデータを取得する関数
    const fetchImages = async () => {
      try {
        const response = await axios.get('https://kaitoportfolio.microcms.io/api/v1/portfolio', {
          headers: {
            'X-API-KEY': 'D47K5TpPZfRzZCyXPPs2YxML9X5uOJyWAQdZ'
          }
        });
        setImages(response.data.contents);
      } catch (error) {
        console.error('Error fetching data from MicroCMS:', error);
      }
    };

    fetchImages();

    const interval = setInterval(() => {
      setShow(false);
      setTimeout(() => {
        setCurrentIndex(prevIndex => (prevIndex + 1) % images.length);
        setShow(true);
      }, 1000); // アニメーション時間（1秒）に合わせる
    }, 20000); // 3秒ごとに切り替え

    return () => clearInterval(interval);
  }, [images.length]);

  useEffect(() => {
    if (images.length > 0) {
      setShuffledBackText(shuffleText(imgBackText));
      gsap.to('.imgBackText .char', {
        opacity: 1,
        duration: 0.5,
        stagger: 0.02,
        ease: 'power2.inOut',
        onComplete: () => {
          gsap.to('.imgBackText .char', {
            opacity: 0,
            duration: 0.5,
            stagger: 0.02,
            ease: 'power2.inOut',
            onComplete: () => {
              setShuffledBackText(imgBackText);
              gsap.to('.imgBackText .char', {
                opacity: 1,
                duration: 0.5,
                stagger: 0.02,
                ease: 'power2.inOut',
              });
            }
          });
        }
      });
    }
  }, [currentIndex, images]);

  if (images.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <div className="imgContainer">
        <h3 className="imgBackText">
          {splitText(shuffledBackText)}
        </h3>
      </div>
      <div className="overviewArea">
        <div className="left">
          <h3 className={`title ${show ? 'show' : ''}`}>
            {images[currentIndex].title}
          </h3>
          <h3 className={`hurigana ${show ? 'show' : ''}`}>
            {images[currentIndex].hurigana}
          </h3>
        </div>
        <div className="right">
          <div className={`place ${show ? 'show' : ''}`}>
            <img src='/img/map.png' alt="map"/>
            <h3>{images[currentIndex].place}</h3>
          </div>
          <h3 className={`day ${show ? 'show' : ''}`}>{images[currentIndex].day}</h3>
        </div>
      </div>
    </div>
  );
};

export default Slideshow;
