import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles/slideshow.css';
import imgBackText from './imgBackText'; // インポート

const splitText = (text) => {
  return text.split('. ').map((line, lineIndex) => (
    <div key={lineIndex} className="line">
      {line}
    </div>
  ));
};

const Slideshow = () => {
  const [images, setImages] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [show, setShow] = useState(true);
  const [visibleLines, setVisibleLines] = useState([]);

  useEffect(() => {
    // MicroCMSからデータを取得する関数
    const fetchImages = async () => {
      try {
        const response = await axios.get('https://kaitoportfolio.microcms.io/api/v1/portfolio', {
          headers: {
            'X-API-KEY': 'D47K5TpPZfRzZCyXPPs2YxML9X5uOJyWAQdZ'
          }
        });
        setImages(response.data.contents);
      } catch (error) {
        console.error('Error fetching data from MicroCMS:', error);
      }
    };

    fetchImages();

    const interval = setInterval(() => {
      setShow(false);
      setTimeout(() => {
        setCurrentIndex(prevIndex => (prevIndex + 1) % images.length);
        setShow(true);
      }, 500); // アニメーション時間（0.5秒）に合わせる
    }, 20000); // 20秒ごとに切り替え

    return () => clearInterval(interval);
  }, [images.length]);

  useEffect(() => {
    const lines = imgBackText.split('. ');
    let currentLine = 0;

    const interval = setInterval(() => {
      setVisibleLines(prevLines => [...prevLines, lines[currentLine]]);
      currentLine += 1;

      if (currentLine >= lines.length) {
        clearInterval(interval);
      }
    }, 500); // 行を表示する間隔

    return () => clearInterval(interval);
  }, []);

  if (images.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <div className="imgContainer">
        <div className="imgBackText">
          {visibleLines.map((line, index) => (
            <div key={index} className="line">
              {line}
            </div>
          ))}
        </div>
      </div>
      <div className="overviewArea">
        <div className="left">
          <h3 className={`title ${show ? 'show' : ''}`}>
            {images[currentIndex].title}
          </h3>
          <h3 className={`hurigana ${show ? 'show' : ''}`}>
            {images[currentIndex].hurigana}
          </h3>
        </div>
        <div className="right">
          <div className={`place ${show ? 'show' : ''}`}>
            <img src='/img/map.png' alt="map"/>
            <h3>{images[currentIndex].place}</h3>
          </div>
          <h3 className={`day ${show ? 'show' : ''}`}>{images[currentIndex].day}</h3>
        </div>
      </div>
    </div>
  );
};

export default Slideshow;
