import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Masonry from 'react-masonry-css';
import '../styles/photo.css';

const Photo = () => {
  const [images, setImages] = useState([]);
  const [groupedImages, setGroupedImages] = useState({});
  const [selectedGenre, setSelectedGenre] = useState(null);
  const [page, setPage] = useState(0);
  const [loading, setLoading] = useState(false);
  const limit = 20; // 1ページあたりの画像数
  const observerRef = useRef();

  useEffect(() => {
    const fetchImages = async () => {
      setLoading(true);
      try {
        const response = await axios.get('/api/v1/photolibrary', {
          headers: {
            'X-API-KEY': 'D47K5TpPZfRzZCyXPPs2YxML9X5uOJyWAQdZ'
          },
          params: {
            offset: page * limit,
            limit: limit
          }
        });
        setImages(prevImages => {
          const newImages = response.data.contents.filter(newImage => 
            !prevImages.some(prevImage => prevImage.id === newImage.id)
          );
          return [...prevImages, ...newImages];
        });
        setLoading(false);
      } catch (error) {
        console.error('Error fetching images:', error);
        setLoading(false);
      }
    };

    fetchImages();
  }, [page]);

  useEffect(() => {
    if (images.length > 0) {
      const grouped = images.reduce((acc, image) => {
        image.genre.forEach(genre => {
          if (!acc[genre]) {
            acc[genre] = [];
          }
          acc[genre].push(image);
        });
        return acc;
      }, {});
      setGroupedImages(grouped);
    }
  }, [images]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting) {
          loadMoreImages();
        }
      },
      { threshold: 1 }
    );

    if (observerRef.current) {
      observer.observe(observerRef.current);
    }

    return () => {
      if (observerRef.current) {
        observer.unobserve(observerRef.current);
      }
    };
  }, []);

  const handleGenreClick = (genre) => {
    setSelectedGenre(genre);
    setPage(0);
    setImages([]);
  };

  const handleResetClick = () => {
    setSelectedGenre(null);
    setPage(0);
    setImages([]);
  };

  const loadMoreImages = () => {
    setPage(prevPage => prevPage + 1);
  };

  const breakpointColumnsObj = {
    default: 4,
    1100: 3,
    700: 2,
    500: 2
  };

  return (
    <div className="photoContainer">
      <div className="genreButtons">
        <p>PHOTOGRAPH</p>
        <div className="right">
          {Object.keys(groupedImages).map((genre, index) => (
            <button key={index} onClick={() => handleGenreClick(genre)}>
              {genre}
            </button>
          ))}
          <button onClick={handleResetClick}>All</button>
        </div>
      </div>
      <Masonry
        breakpointCols={breakpointColumnsObj}
        className="my-masonry-grid"
        columnClassName="my-masonry-grid_column"
      >
        {selectedGenre
          ? groupedImages[selectedGenre]?.map((image, imgIndex) => (
              <div key={imgIndex} className="gallery-item">
                <img src={image.photo.url} alt={`Photo ${imgIndex + 1}`} />
              </div>
            ))
          : images.map((image, imgIndex) => (
              <div key={imgIndex} className="gallery-item">
                <img src={image.photo.url} alt={`Photo ${imgIndex + 1}`} />
              </div>
            ))}
      </Masonry>
      <div ref={observerRef} className="loadMoreContainer">
        {loading && <p>Loading...</p>}
      </div>
    </div>
  );
};

export default Photo;
