import React, { useState, useEffect } from 'react';
import axios from 'axios';
import gsap from 'gsap';
import './styles/slideshow.css';

const splitText = (text) => {
  return text.split('').map((char, index) => (
    <span key={index} className="char">
      {char}
    </span>
  ));
};

const shuffleText = (chars) => {
  gsap.fromTo(
    chars,
    {
      opacity: 0,
    },
    {
      opacity: 1,
      duration: 0.5,
      stagger: {
        amount: 0.5,
        from: 'random',
      },
      ease: 'power2.inOut',
    }
  );
};

const Slideshow = () => {
  const [images, setImages] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [show, setShow] = useState(true);

  useEffect(() => {
    const fetchImages = async () => {
      try {
        const response = await axios.get('https://kaitoportfolio.microcms.io/api/v1/portfolio', {
          headers: {
            'X-API-KEY': 'D47K5TpPZfRzZCyXPPs2YxML9X5uOJyWAQdZ',
          },
        });
        setImages(response.data.contents);
      } catch (error) {
        console.error('Error fetching data from MicroCMS:', error);
      }
    };

    fetchImages();

    const interval = setInterval(() => {
      setShow(false);
      setTimeout(() => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
        setShow(true);
      }, 1000);
    }, 2000);

    return () => clearInterval(interval);
  }, [images.length]);

  useEffect(() => {
    if (images.length > 0) {
      const titleChars = document.querySelectorAll('.title .char');
      const huriganaChars = document.querySelectorAll('.hurigana .char');
      const placeChars = document.querySelectorAll('.place .char');
      const dayChars = document.querySelectorAll('.day .char');

      shuffleText(titleChars);
      shuffleText(huriganaChars);
      shuffleText(placeChars);
      shuffleText(dayChars);
    }
  }, [currentIndex, show]);

  if (images.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <div className="imgContainer">
        <img
          src={images[currentIndex].src.url}
          alt={images[currentIndex].title}
          className={`imgArea ${show ? 'show' : ''}`}
        />
      </div>
      <div className="overviewArea">
        <div className="left">
          <h2 className={`title ${show ? 'show' : ''}`}>
            {splitText(images[currentIndex].title)}
          </h2>
          <h3 className={`hurigana ${show ? 'show' : ''}`}>
            {splitText(images[currentIndex].hurigana)}
          </h3>
        </div>
        <div className="right">
          <div className={`place ${show ? 'show' : ''}`}>
            <img src="/img/map.png" alt="map" />
            <h2>{splitText(images[currentIndex].place)}</h2>
          </div>
          <h2 className={`day ${show ? 'show' : ''}`}>
            {splitText(images[currentIndex].day)}
          </h2>
        </div>
      </div>
    </div>
  );
};

export default Slideshow;
