import React, { useState, useEffect } from 'react';
import axios from 'axios';
import gsap from 'gsap';
import './styles/slideshow.css';

const shuffleText = (text) => {
  let shuffled = text.split('');
  for (let i = shuffled.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
  }
  return shuffled.join('');
};

const splitText = (text) => {
  return text.split('').map((char, index) => (
    <span key={index} className="char">
      {char}
    </span>
  ));
};

const Slideshow = () => {
  const [images, setImages] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [show, setShow] = useState(true);
  const [shuffledTitle, setShuffledTitle] = useState('');
  const [shuffledHurigana, setShuffledHurigana] = useState('');
  const [shuffledPlace, setShuffledPlace] = useState('');
  const [shuffledDay, setShuffledDay] = useState('');

  useEffect(() => {
    const fetchImages = async () => {
      try {
        const response = await axios.get('https://kaitoportfolio.microcms.io/api/v1/portfolio', {
          headers: {
            'X-API-KEY': 'D47K5TpPZfRzZCyXPPs2YxML9X5uOJyWAQdZ',
          },
        });
        setImages(response.data.contents);
      } catch (error) {
        console.error('Error fetching data from MicroCMS:', error);
      }
    };

    fetchImages();

    const interval = setInterval(() => {
      setShow(false);
      setTimeout(() => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
        setShow(true);
      }, 1000);
    }, 20000);

    return () => clearInterval(interval);
  }, [images.length]);

  useEffect(() => {
    if (images.length > 0) {
      setShuffledTitle(shuffleText(images[currentIndex].title));
      setShuffledHurigana(shuffleText(images[currentIndex].hurigana));
      setShuffledPlace(shuffleText(images[currentIndex].place));
      setShuffledDay(shuffleText(images[currentIndex].day));

      const charsTitle = document.querySelectorAll('.title .char');
      const charsHurigana = document.querySelectorAll('.hurigana .char');
      const charsPlace = document.querySelectorAll('.place .char');
      const charsDay = document.querySelectorAll('.day .char');

      gsap.to(charsTitle, {
        opacity: 1,
        duration: 0.1,
        stagger: 0.02,
        ease: 'power2.inOut',
        onComplete: () => {
          setShuffledTitle(images[currentIndex].title);
          gsap.fromTo(charsTitle, { opacity: 0 }, { opacity: 1, duration: 0.1, stagger: 0.02 });
        }
      });

      gsap.to(charsHurigana, {
        opacity: 1,
        duration: 0.1,
        stagger: 0.02,
        ease: 'power2.inOut',
        onComplete: () => {
          setShuffledHurigana(images[currentIndex].hurigana);
          gsap.fromTo(charsHurigana, { opacity: 0 }, { opacity: 1, duration: 0.1, stagger: 0.02 });
        }
      });

      gsap.to(charsPlace, {
        opacity: 1,
        duration: 0.1,
        stagger: 0.02,
        ease: 'power2.inOut',
        onComplete: () => {
          setShuffledPlace(images[currentIndex].place);
          gsap.fromTo(charsPlace, { opacity: 0 }, { opacity: 1, duration: 0.1, stagger: 0.02 });
        }
      });

      gsap.to(charsDay, {
        opacity: 1,
        duration: 0.1,
        stagger: 0.02,
        ease: 'power2.inOut',
        onComplete: () => {
          setShuffledDay(images[currentIndex].day);
          gsap.fromTo(charsDay, { opacity: 0 }, { opacity: 1, duration: 0.1, stagger: 0.02 });
        }
      });
    }
  }, [currentIndex, show]);

  if (images.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <div className="imgContainer">
        <img
          src={images[currentIndex].src.url}
          alt={images[currentIndex].title}
          className={`imgArea ${show ? 'show' : ''}`}
        />
      </div>
      <div className="overviewArea">
        <div className="left">
          <h2 className={`title ${show ? 'show' : ''}`}>
            {splitText(shuffledTitle)}
          </h2>
          <h3 className={`hurigana ${show ? 'show' : ''}`}>
            {splitText(shuffledHurigana)}
          </h3>
        </div>
        <div className="right">
          <div className={`place ${show ? 'show' : ''}`}>
            <img src="/img/map.png" alt="map" />
            <h2>{splitText(shuffledPlace)}</h2>
          </div>
          <h2 className={`day ${show ? 'show' : ''}`}>
            {splitText(shuffledDay)}
          </h2>
        </div>
      </div>
    </div>
  );
};

export default Slideshow;
