import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import './styles/slideshow.css';

gsap.registerPlugin(ScrollTrigger);

const Slideshow = () => {
  const [images, setImages] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const slideshowRef = useRef(null);

  useEffect(() => {
    // MicroCMSからデータを取得する関数
    const fetchImages = async () => {
      try {
        const response = await axios.get('https://kaitoportfolio.microcms.io/api/v1/portfolio', {
          headers: {
            'X-API-KEY': 'D47K5TpPZfRzZCyXPPs2YxML9X5uOJyWAQdZ'
          }
        });
        setImages(response.data.contents);
      } catch (error) {
        console.error('Error fetching data from MicroCMS:', error);
      }
    };

    fetchImages();
  }, []);

  useEffect(() => {
    if (images.length === 0) return;

    const tl = gsap.timeline({
      scrollTrigger: {
        trigger: slideshowRef.current,
        start: "top center",
        end: "bottom center",
        scrub: true,
        onEnter: () => handleNextSlide(),
        onEnterBack: () => handlePrevSlide()
      }
    });

    tl.to(".imgArea", { opacity: 0, duration: 1 })
      .to(".imgArea", { opacity: 1, duration: 1 });

    return () => {
      if (tl.scrollTrigger) tl.scrollTrigger.kill();
    };
  }, [images]);

  const handleNextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  return (
    <div className="slideshow-container" ref={slideshowRef}>
      {images.map((image, index) => (
        <img
          key={index}
          src={image.src.url}
          alt={image.title}
          className={`imgArea ${index === currentIndex ? 'active' : ''}`}
        />
      ))}
    </div>
  );
};

export default Slideshow;
